import React, { useState, useEffect } from 'react';
import { Mail, Settings, RefreshCw, AlertCircle, CheckCircle } from 'lucide-react';
import axios from 'axios';

const EmailSettings = () => {
  const [config, setConfig] = useState({
    user: '',
    password: '',
    host: 'imap.gmail.com',
    port: 993
  });
  const [logs, setLogs] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [testing, setTesting] = useState(false);
  const [processing, setProcessing] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [configResponse, logsResponse, statsResponse] = await Promise.all([
        axios.get('/api/email/config'),
        axios.get('/api/email/log'),
        axios.get('/api/email/stats')
      ]);

      setConfig(configResponse.data);
      setLogs(logsResponse.data.logs);
      setStats(statsResponse.data);
    } catch (error) {
      console.error('Error fetching email data:', error);
    } finally {
      setLoading(false);
    }
  };

  const testConnection = async () => {
    setTesting(true);
    try {
      const response = await axios.post('/api/email/test-connection', config);
      if (response.data.success) {
        alert('Email connection successful!');
      } else {
        alert('Email connection failed: ' + response.data.message);
      }
    } catch (error) {
      console.error('Error testing connection:', error);
      alert('Email connection failed: ' + error.response?.data?.message || error.message);
    } finally {
      setTesting(false);
    }
  };

  const processEmails = async () => {
    setProcessing(true);
    try {
      const response = await axios.post('/api/email/process');
      alert(`Processed ${response.data.emails.length} emails`);
      fetchData();
    } catch (error) {
      console.error('Error processing emails:', error);
      alert('Error processing emails: ' + error.response?.data?.error || error.message);
    } finally {
      setProcessing(false);
    }
  };

  const handleConfigChange = (field, value) => {
    setConfig(prev => ({
      ...prev,
      [field]: value
    }));
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-3xl font-bold text-gray-900">Email Settings</h1>
        <p className="text-gray-600 mt-2">
          Configure email processing and monitor activity
        </p>
      </div>

      {/* Configuration */}
      <div className="card">
        <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
          <Settings className="w-5 h-5 mr-2" />
          Email Configuration
        </h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Email Address
            </label>
            <input
              type="email"
              value={config.user}
              onChange={(e) => handleConfigChange('user', e.target.value)}
              className="input-field"
              placeholder="your-email@gmail.com"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Password/App Password
            </label>
            <input
              type="password"
              value={config.password}
              onChange={(e) => handleConfigChange('password', e.target.value)}
              className="input-field"
              placeholder="Enter your password or app password"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              IMAP Host
            </label>
            <input
              type="text"
              value={config.host}
              onChange={(e) => handleConfigChange('host', e.target.value)}
              className="input-field"
              placeholder="imap.gmail.com"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Port
            </label>
            <input
              type="number"
              value={config.port}
              onChange={(e) => handleConfigChange('port', parseInt(e.target.value))}
              className="input-field"
              placeholder="993"
            />
          </div>
        </div>

        <div className="flex items-center space-x-4 mt-6">
          <button
            onClick={testConnection}
            disabled={testing}
            className="btn-secondary flex items-center space-x-2"
          >
            {testing ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-gray-600"></div>
                <span>Testing...</span>
              </>
            ) : (
              <>
                <Mail className="w-4 h-4" />
                <span>Test Connection</span>
              </>
            )}
          </button>

          <button
            onClick={processEmails}
            disabled={processing}
            className="btn-primary flex items-center space-x-2"
          >
            {processing ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                <span>Processing...</span>
              </>
            ) : (
              <>
                <RefreshCw className="w-4 h-4" />
                <span>Process Emails</span>
              </>
            )}
          </button>
        </div>

        <div className="mt-4 p-4 bg-blue-50 rounded-lg">
          <h4 className="font-medium text-blue-900 mb-2">Setup Instructions:</h4>
          <ul className="text-sm text-blue-800 space-y-1">
            <li>• For Gmail: Use "imap.gmail.com" as host and port 993</li>
            <li>• Enable 2-factor authentication and generate an App Password</li>
            <li>• Use the App Password instead of your regular password</li>
            <li>• The system will only process emails with PDF attachments</li>
          </ul>
        </div>
      </div>

      {/* Statistics */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="card">
            <div className="flex items-center">
              <div className="p-2 bg-green-100 rounded-lg">
                <CheckCircle className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Successful</p>
                <p className="text-2xl font-bold text-gray-900">
                  {stats.summary?.find(s => s.status === 'processed')?.count || 0}
                </p>
              </div>
            </div>
          </div>

          <div className="card">
            <div className="flex items-center">
              <div className="p-2 bg-red-100 rounded-lg">
                <AlertCircle className="w-6 h-6 text-red-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Failed</p>
                <p className="text-2xl font-bold text-gray-900">
                  {stats.summary?.find(s => s.status === 'error')?.count || 0}
                </p>
              </div>
            </div>
          </div>

          <div className="card">
            <div className="flex items-center">
              <div className="p-2 bg-blue-100 rounded-lg">
                <Mail className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Processed</p>
                <p className="text-2xl font-bold text-gray-900">
                  {stats.summary?.reduce((sum, s) => sum + s.count, 0) || 0}
                </p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Recent Activity */}
      <div className="card">
        <h2 className="text-xl font-semibold text-gray-900 mb-4">
          Recent Email Processing Log
        </h2>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="table-header">Date</th>
                <th className="table-header">Email ID</th>
                <th className="table-header">Status</th>
                <th className="table-header">Error Message</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {logs.map((log) => (
                <tr key={log.id} className="hover:bg-gray-50">
                  <td className="table-cell">
                    {new Date(log.processed_at).toLocaleString()}
                  </td>
                  <td className="table-cell font-mono text-sm">
                    {log.email_id}
                  </td>
                  <td className="table-cell">
                    <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                      log.status === 'processed' 
                        ? 'bg-green-100 text-green-800'
                        : 'bg-red-100 text-red-800'
                    }`}>
                      {log.status === 'processed' ? (
                        <CheckCircle className="w-3 h-3 mr-1" />
                      ) : (
                        <AlertCircle className="w-3 h-3 mr-1" />
                      )}
                      {log.status}
                    </span>
                  </td>
                  <td className="table-cell text-sm text-gray-600">
                    {log.error_message || '-'}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {logs.length === 0 && (
          <div className="text-center py-8 text-gray-500">
            No email processing logs found
          </div>
        )}
      </div>
    </div>
  );
};

export default EmailSettings;
